const mailInput=document.querySelector("input"),mailRegExp=/^([a-zA-z0-9._%+-]+)@([a-zA-z0-9.-]+)\.([a-zA-z]{2,})$/;let isLiveMailVerifierInitialized=!1;function initializeLiveMailVerifier(){mailInput.addEventListener("keyup",(i=>{const e=i.target.value;mailRegExp.test(e)?i.target.classList.remove("form__mail--invalid"):i.target.classList.add("form__mail--invalid")}))}const formElement=document.querySelector("form"),formSection=document.querySelector(".main__form");formElement.addEventListener("submit",(i=>{i.preventDefault(),!1===isLiveMailVerifierInitialized&&(initializeLiveMailVerifier(),isLiveMailVerifierInitialized=!0),mailRegExp.test(mailInput.value)?(mailInput.value="",formSection.classList.add("main__form--success")):mailInput.classList.add("form__mail--invalid")})),document.querySelector(".success-state__dismiss-btn").addEventListener("click",(()=>{formSection.classList.remove("main__form--success")}));